private function onLoaded(evt:Event):void
{
var domain:ApplicationDomain = evt.target.contentLoaderInfo.applicationDomain as ApplicationDomain;
//也可以
//var c:Class = getDefinitionByName(“A”) as Class;
var c:Class = domain.getDefinition(“A”) as Class;
var obj:Object = new c();
//obj调用该类中的各种函数
}

